-- ----------------------------
-- Procedure structure for UpdatePostRanking
-- ----------------------------
DROP PROCEDURE IF EXISTS `UpdatePostRanking`;
delimiter ;;
CREATE PROCEDURE `UpdatePostRanking`()
BEGIN
    -- 计算并更新帖子排名
    SELECT 
        post_id,
        ROW_NUMBER() OVER (ORDER BY like_count + comment_count + view_count DESC) as day_rank,
        ROW_NUMBER() OVER (ORDER BY like_count + comment_count + view_count DESC) as week_rank,
        ROW_NUMBER() OVER (ORDER BY like_count + comment_count + view_count DESC) as month_rank,
        ROW_NUMBER() OVER (ORDER BY like_count + comment_count + view_count DESC) as year_rank,
        ROW_NUMBER() OVER (ORDER BY like_count + comment_count + view_count DESC) as total_rank,
        NOW()
    FROM biz_post 
    WHERE status = '0' AND del_flag = '0'
    ON DUPLICATE KEY UPDATE
        total_rank = VALUES(total_rank),
        update_time = VALUES(update_time);
END
;;
delimiter ;

