DROP TRIGGER IF EXISTS `tr_comment_parent_validation_insert`;
delimiter ;;
CREATE TRIGGER `tr_comment_parent_validation_insert` BEFORE INSERT ON `biz_comment` FOR EACH ROW BEGIN
    DECLARE parent_post_id INT;
    DECLARE parent_game_id INT;
    
    -- 检查是否自引用（防止parent_id等于自己的comment_id）
    IF NEW.parent_id IS NOT NULL AND NEW.parent_id = NEW.comment_id THEN
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = '评论不能回复自己';
    END IF;
    
    -- 如果指定了父评论ID，则检查父评论的post_id和game_id
    IF NEW.parent_id IS NOT NULL THEN
        -- 获取父评论的post_id和game_id
        SELECT post_id, game_id INTO parent_post_id, parent_game_id
        FROM biz_comment 
        WHERE comment_id = NEW.parent_id;
        
        -- 如果父评论不存在，抛出错误
        IF parent_post_id IS NULL THEN
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = '父评论不存在';
        END IF;
        
        -- 检查post_id是否一致
        IF parent_post_id != NEW.post_id THEN
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = '父评论和子评论必须在同一帖子下';
        END IF;
        
        -- 检查game_id是否一致（考虑NULL值的情况）
        IF (parent_game_id IS NULL AND NEW.game_id IS NOT NULL) OR 
           (parent_game_id IS NOT NULL AND NEW.game_id IS NULL) OR
           (parent_game_id IS NOT NULL AND NEW.game_id IS NOT NULL AND parent_game_id != NEW.game_id) THEN
            SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = '父评论和子评论必须在同一游戏下';
        END IF;
    END IF;
END
;;
delimiter ;

