delimiter ;;
CREATE TRIGGER `tr_comment_prevent_circular_reference_insert` BEFORE INSERT ON `biz_comment` FOR EACH ROW BEGIN
    DECLARE current_parent_id INT;
    DECLARE loop_counter INT DEFAULT 0;
    DECLARE max_depth INT DEFAULT 100; -- 防止无限循环，设置最大深度
    
    -- 如果指定了父评论ID，检查是否会形成循环引用
    IF NEW.parent_id IS NOT NULL THEN
        SET current_parent_id = NEW.parent_id;
        
