-- ----------------------------
DROP TRIGGER IF EXISTS `tr_comment_count_update`;
delimiter ;;
CREATE TRIGGER `tr_comment_count_update` AFTER UPDATE ON `biz_comment` FOR EACH ROW BEGIN
    DECLARE old_valid BOOLEAN DEFAULT FALSE;
    DECLARE new_valid BOOLEAN DEFAULT FALSE;
    
    -- 判断更新前评论是否有效（状态正常且未删除）
    SET old_valid = (OLD.status = '0' AND OLD.del_flag = '0');
    
    -- 判断更新后评论是否有效
    SET new_valid = (NEW.status = '0' AND NEW.del_flag = '0');
    
    -- 如果有效性发生变化，则更新帖子评论数
    IF old_valid != new_valid THEN
        IF new_valid THEN
            -- 评论变为有效，增加计数
            UPDATE biz_post 
            SET comment_count = comment_count + 1,
                update_time = NOW()
            WHERE post_id = NEW.post_id;
        ELSE
            -- 评论变为无效，减少计数
            UPDATE biz_post 
            SET comment_count = GREATEST(comment_count - 1, 0),
                update_time = NOW()
            WHERE post_id = NEW.post_id;
        END IF;
    END IF;
END
;;
delimiter ;

