    -- 只有当被删除的评论原本是有效的时才减少计数
    IF OLD.status = '0' AND OLD.del_flag = '0' THEN
        UPDATE biz_post 
        SET comment_count = GREATEST(comment_count - 1, 0),
            update_time = NOW()
